* {
  margin: 0;
  padding: 0; }

body,
html {
  background: #303036;
  width: 100%;
  height: 100%; }

.btnm,
.btnn {
  position: fixed;
  top: 16px;
  cursor: pointer;
  left: 50%;
  transform: translateX(-50%); }
  .btnm span,
  .btnn span {
    width: 40px;
    height: 2px;
    display: block;
    background: white;
    margin: 8px;
    transition: 0.4s all; }

.btnn span {
  background: #303036; }

.menu {
  background: #30bced;
  height: 0;
  width: 100%;
  position: fixed;
  top: 0;
  transition: 0.4s all; }

.show {
  height: 70px;
  background: #30bced;
  transition: 0.4s all;
  text-align: center; }
  .show a {
    display: inline-block;
    margin: 0 10px;
    cursor: pointer;
    line-height: 80px;
    font-size: 20px; }

#l1 {
  margin-right: 70px; }

#l2 {
  margin-left: 70px; }

.hidden {
  text-decoration: none;
  position: relative;
  opacity: 0; }

.showed {
  transition: 0.7s all; }

.linkWidth {
  display: inline-block;
  width: 200px; }

.intro {
  width: 100%;
  text-align: center;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%); }
  .intro span {
    text-transform: uppercase;
    display: block; }

.text1 {
  color: #ffffff;
  font-size: 60px;
  font-weight: 700;
  letter-spacing: 8px;
  margin-bottom: 20px;
  background: #303036;
  position: relative;
  animation: text 3s 1; }

.text2 {
  font-size: 30px;
  color: #30bced; }

@keyframes text {
  0% {
    color: #303036;
    margin-bottom: -40px; }
  30% {
    letter-spacing: 25px;
    margin-bottom: -40px; }
  85% {
    letter-spacing: 8px;
    margin-bottom: -40px; } }
