// GENERAL

* {
  margin: 0;
  padding: 0;
}
body,
html {
  background: url(../img/prism.png);
  width: 100%;
  height: 100%;
}
// HEADER
.btnm,
.btnn {
  position: fixed;
  top: 16px;
  cursor: pointer;
  left: 50%;
  transform: translateX(-50%);
  span {
    width: 40px;
    height: 2px;
    display: block;
    background: white;
    margin: 8px;
    transition: 0.4s all;
  }
}
.btnn span {
  background: #303036;
}
.menu {
  background: #30bced;
  height: 0;
  width: 100%;
  position: fixed;
  top: 0;
  transition: 0.4s all;
}
.show {
  height: 70px;
  background: #30bced;
  transition: 0.4s all;
  text-align: center;
  a {
    display: inline-block;
    margin: 0 10px;
    cursor: pointer;
    line-height: 80px;
    font-size: 20px;
  }
  a:hover {
    opacity: 0.5;
  }
}
#l1 {
  margin-right: 70px;
}
#l2 {
  margin-left: 70px;
}
.hidden {
  text-decoration: none;
  position: relative;
  opacity: 0;
}
.showed {
  transition: 0.7s all;
}
.linkWidth {
  display: inline-block;
  width: 200px;
}

// INTRO
.intro {
  width: 100%;
  text-align: center;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  span {
    text-transform: uppercase;
    display: block;
  }
}
.text1 {
  color: #ffffff;
  font-size: 60px;
  font-weight: 700;
  letter-spacing: 8px;
  margin-bottom: 20px;
  // background: url(../img/prism.png);
  position: relative;
  animation: text 1.5s 1;
}
.text2 {
  font-size: 30px;
  color: #30bced;
  opacity: 0;
  animation-name: junior;
  animation-duration: 2s;
  animation-delay: 1.2s;
  animation-fill-mode: forwards;
}
@keyframes text {
  0% {
    color: #303036;
    margin-bottom: -40px;
  }
  30% {
    letter-spacing: 25px;
    margin-bottom: -40px;
  }
  85% {
    letter-spacing: 8px;
    margin-bottom: -40px;
  }
}

@keyframes junior {
  100% {
    opacity: 1;
  }
}

.text3 {
  opacity: 0;
  font-weight: bold;
  text-transform: uppercase;
  color: #30bced;
  border: 1px solid #30bced;
  background: #303036;
  display: inline-block;
  margin-top: 20px;
  padding: 18px 50px;
  border-radius: 3px;
  transition: all 0.3s ease-in 0s;
  animation-name: buttonPop;
  animation-duration: 2s;
  animation-delay: 1.7s;
  animation-fill-mode: forwards;
}

.text3:hover {
  background: #30bced;
  color: #303036;
  text-decoration: none;
}

@keyframes buttonPop {
  100% {
    opacity: 1;
  }
}

