/* GENERAL */

body {
  font-family: "Lato", sans-serif;
}

h1,
h2,
h3,
h4,
h5,
h6 {
  font-weight: bold;
}

img {
  max-width: 100%;
  height: auto;
}

section {
  padding: 45px 0 80px 0;
  @media screen and (max-width: 768px) {
    overflow: hidden;
  }
  .heading {
    text-align: center;
    padding-bottom: 40px;
    h2 {
      text-transform: uppercase;
    }
  }
}

.red-divider {
  width: 100px;
  height: 2px;
  background: #303036;
  margin: 0 auto;
}

.white-divider {
  width: 100px;
  height: 2px;
  background: #30bced;
  margin: 0 auto;
}

/* MENU */

.navbar {
  background: #303036;
  font-size: 12px;
  letter-spacing: 4px;
  text-transform: uppercase;
  font-weight: bold;
  z-index: 99999;
  .navbar-nav {
    float: none;
    vertical-align: top;
    @media screen and (min-width: 768px) {
      display: inline-block;
    }
  }
  .navbar-collapse {
    text-align: center;
  }
  li a {
    color: #30bced !important;
  }
}

.navbar-nav li {
  a:hover,
  &.active a {
    color: #303036 !important;
    background-color: #30bced !important;
  }
}

.navbar-default .navbar-toggle {
  border-style: none;
}

.icon-bar {
  background-color: #30bced !important;
}

/* ABOUT */

#about {
  padding-top: 100px;
}

.profile-picture {
  text-align: center;
  margin: 0 auto;
  float: none;
}

#about .heading {
  h1 {
    color: #30bced;
    text-transform: uppercase;
  }
  h3 {
    color: #fc5130;
  }
}

.button1 {
  font-weight: bold;
  text-transform: uppercase;
  color: #30bced;
  border: 1px solid #30bced;
  display: inline-block;
  margin-top: 20px;
  padding: 18px 50px;
  border-radius: 3px;
  transition: all 0.3s ease-in 0s;
  &:hover {
    background: #30bced;
    color: #303036;
    text-decoration: none;
  }
}

.fab {
  color: white;
  margin: 10px;
  &:hover {
    color: #30bced;
  }
}

/* SKILLS */

#skills {
  background: #30bced;
  .heading h2 {
    color: #303036;
  }
  .progress {
    height: 100%;
    margin: 30px 20px;
    backface-visibility: #ddd;
  }
  .progress-bar {
    background: #303036;
  }
  .progress h5 {
    text-shadow: 1px 1px 1px #000;
  }
  .comp {
    width: 100px;
    margin: 32px;
    display: inline-block;
  }
  .comp-container {
    text-align: center;
  }
}

/* EXPERIENCE */

#experience {
  ul {
    list-style: none;
  }
  .heading h2 {
    color: #30bced;
  }
}

.timeline {
  padding: 30px 10px;
  position: relative;
  &:before {
    top: 0;
    bottom: 0;
    position: absolute;
    content: " ";
    width: 5px;
    background-color: #30bced;
    left: 50%;
    margin-left: -2.5px;
  }
  > li {
    margin-bottom: 20px;
    position: relative;
    &:after {
      clear: both;
    }
    &:before,
    &:after {
      content: " ";
      display: table;
    }
  }
  li .timeline-badge {
    color: #fc5130;
    width: 50px;
    line-height: 50px;
    font-size: 20px;
    text-align: center;
    position: absolute;
    top: 16px;
    left: 50%;
    margin-left: -25px;
    background-color: #ddd;
    border-radius: 50%;
  }
}

.timeline-panel-container {
  width: 50%;
  float: left;
}

.timeline-panel-container-inverted {
  width: 50%;
  float: right;
}

.timeline-panel {
  width: 85%;
  float: right;
  margin-right: 40px;
  border: 1px solid #d4d4d4;
  border-radius: 7px;
  padding: 20px 25px;
  position: relative;
  box-shadow: 0 1px 6px rgba(0, 0, 0, 0.175);
  background: #ddd;
}

.timeline-panel-container-inverted .timeline-panel {
  float: left;
  margin-left: 40px;
}

.timeline-panel:after {
  position: absolute;
  top: 27px;
  right: -14px;
  display: inline-block;
  border-top: 14px solid transparent;
  border-left: 14px solid #ddd;
  border-right: 0 solid #ddd;
  border-bottom: 14px solid transparent;
  content: " ";
}

.timeline-panel-container-inverted .timeline-panel:after {
  border-left-width: 0;
  border-right-width: 14px;
  left: -14px;
  right: auto;
}

.timeline-heading {
  h3 {
    margin-top: 5px;
    font-size: 20px;
    @media screen and (max-width: 768px) {
      font-size: 16px;
    }
  }
  h4 {
    color: #fc5130;
  }
}

/* EDUCATION */

#education {
  background: #30bced;
  .heading h2 {
    color: #303036;
  }
}

.education-block {
  width: 80%;
  margin: 0 auto;
  text-align: center;
  padding: 30px;
  background: #ddd;
  border-radius: 10px;
  border: 2px solid #ccc;
  margin-bottom: 20px;
  h5 {
    color: #888;
    font-size: 15px;
    margin-bottom: 15px;
  }
  .glyphicon {
    font-size: 40px;
  }
  h3 {
    color: #fc5130;
  }
  h4 {
    margin-bottom: 20px;
  }
  p {
    font-weight: bold;
  }
  .red-divider {
    margin-bottom: 20px;
  }
}

/* PORTFOLIO */

#portfolio {
  .heading h2 {
    color: #30bced;
  }
  .thumbnail img {
    width: 100%;
    height: 100%;
    min-height: 233.33px;
    filter: grayscale(100%);
    -webkit-filter: grayscale(100%);
    &:hover {
      filter: none;
      -webkit-filter: none;
    }
  }
}

/* RECOMMANDATIONS */

#recommandations {
  background: #30bced;
  ol {
    list-style: none;
  }
  .heading h2 {
    color: #303036;
  }
}

.carousel-control {
  &.left,
  &.right {
    background-image: none;
    color: #303036;
  }
}

.carousel-indicators {
  bottom: 0px;
  li {
    border-color: #303036;
    &.active {
      background-color: #303036;
    }
  }
}

#myCarousel .item {
  padding: 40px 30px;
  h3 {
    color: #fc5130;
    font-style: italic;
    text-shadow: 0.5px 0.5px 0.5px #303036;
  }
}

/* FOOTER */

footer {
  padding: 20px 0 40px;
  .glyphicon {
    font-size: 25px;
    margin: 20px;
    color: #fc5130;
    height: 30px;
    &:hover {
      font-size: 27px;
    }
  }
  h5 {
    color: #30bced;
    font-weight: normal;
  }
}

//CONTACT INFO

.contact-info {
  font-family: "montserrat", sans-serif;
  position: fixed;
  top: 40%;
  z-index: 99998;
  left: 0;
}
.option {
  cursor: pointer;
  position: relative;
  i {
    display: block;
    width: 60px;
    text-align: center;
    height: 60px;
    line-height: 60px;
    background: #f1f1f1;
    color: #b9b9b9;
    font-size: 20px;
    transition: 0.6s;
    @media screen and (max-width: 768px) {
      width: 30px;
    }
  }
  &:hover i {
    color: #30bced;
  }
  &:hover .info {
    left: 60px;
    @media screen and (max-width: 768px) {
      left: 30px;
    }
  }
}
.info {
  position: absolute;
  height: 60px;
  width: 200px;
  background: #1b1b1b;
  top: 0;
  z-index: -1;
  left: -140px;
  color: #30bced;
  line-height: 60px;
  text-align: center;
  transition: 0.6s;
  @media screen and (max-width: 768px) {
    left: -170px;
  }
}
